inline suspend fun <E, R> Flow<AsyncResult<R>>.toEither(errorTransform: (Error) -> E = {
      it.metadataOrNull<E>()
          ?: error(
              "Unexpected error type: $it. " +
                  "Expected ${E::class.simpleName}. Provide your own errorTransform function to handle this case.",
          )
    }): Either<E, R>

